function updateButtonState(isRunning){let button=document.getElementById("toggleButton"),statusText=document.getElementById("statusText");isRunning?(button.classList.replace("start","stop"),button.textContent="Stop",statusText.textContent="Status: Running"):(button.classList.replace("stop","start"),button.textContent="Start",statusText.textContent="Status: Not Running")}document.addEventListener("DOMContentLoaded",(function(){chrome.runtime.sendMessage({action:"queryState"},(function(response){updateButtonState(response.isSwitching)}))})),document.getElementById("toggleButton").addEventListener("click",(function(){let isRunning="Start"===document.getElementById("toggleButton").textContent,intervalInput=document.getElementById("interval").value,interval=intervalInput?1e3*parseInt(intervalInput):null;updateButtonState(isRunning),chrome.runtime.sendMessage({action:"toggleSwitching",value:isRunning?interval:null})}));